sp_widget.client_script:
api.controller=function($scope) {
    var c = this;
    c.selectedStep=1;
    var gform=$scope.page.g_form;
    c.setTabIndex=function(index){
        var oldValue=gform.getValue('currentSection');
        gform.setValue('current_section',index);
        c.selectedStep=index;
		};
}

